addkills.example:
// Add 5 kills to player
local.player addkills 5
// Remove 5 kills from player
local.player addkills -5

atan.example:
// Breakthrough
local.result = atan local.y local.x

// Reborn
local.result = atan local.x
// Result: Principal arc tangent of x, in the interval [-pi/2,+pi/2] radians.

avelocity.example:
// Entity
local.angular_velocity = local.entity.avelocity

// Projectile
local.projectile avelocity "random" 90 "random" 90 60

bombs_planted.example:
level bombs_planted 1
// Or
level.bombs_planted++
// Or
level.bombs_planted = 1
local.bombs_planted = level.bombs_planted

dontdropweapons.example:
// Sentient
local.sentient dontdropweapons

// AISpawnPoint
local.result = local.AISpawnPoint.dontdropweapons

fadein.example:
// Entity
local.entity fadein 3.5 1.0
// ScriptThread
fadein 2 0 0 0 1
// SmokeGrenade (SH & BT)
local.smoke_grenade fadein 2.5

fov.example:
local.camera fov 110 2

// Actor
local.actor fov 120
// Or
local.actor.fov = 120
local.fov = local.actor.fov

getcontrollerangles.example:
local.controllerangles = local.player getcontrollerangles 0

// Is player leaning?  If so, local.lean will be < or > than 0
local.lean = local.controllerangles[2]

netname.example:
// Breakthrough
local.player_name = local.player.netname
// Reborn
local.player_name = netname local.player

print.example:
print "Hello"
println " World!"
// Prints: Hello World!

sighttrace.example:
local.result = sighttrace ( 550 -783 140 ) ( 1220 340 78 ) 1
// Or
local.result = local.entity sighttrace local.entity.origin ( 1220 340 78 ) 0 ( -16 -16 16 ) ( 16 16 128 )

size.example:
local.result = $player.size // Returns the number of players
// Or
local.result = ("abcde").size // Returns: 5

spawn.example:
local.entity = spawn "script_object" model "models/animate/fire.tik" origin ( 0 0 0 )
// Or
spawn "script_object" model "models/animate/fire.tik" origin ( 0 0 0 )

thread.example:
// ScriptThread
thread label
// Or
local.result = thread label

// Listener
local.listener thread label

time.example:
// Door, ScriptSlave
local.ent time 1

// Level
local.current_time = level.time

trace.example:
local.result = trace ( 550 -783 140 ) ( 1220 340 78 )
// Or
local.result = local.entity trace local.entity.origin ( 1220 340 78 ) 0 ( -16 -16 16 ) ( 16 16 128 )

turnspeed.example:
// Actor
local.actor turnspeed 50
// Or
local.actor.turnspeed = 50
local.turnspeed = local.actor.turnspeed

// TurretGun, VehicleTurretGun
local.turret turnspeed 100

voicetype.example:
// Actor
local.actor voicetype "a"
// Or
local.actor.voicetype = "a"
local.voicetype = local.actor.voicetype

// Player
local.player voicetype "manon"

waitthread.example:
waitthread label
// Or
local.listener waitthread global/script.scr::label
// Or
local.result = waitthread label
