adddeaths.example:
// Add 5 deaths to player
local.player adddeaths 5
// Remove 5 deaths from player
local.player adddeaths -5

addkills.example:
// Add 5 kills to player
local.player addkills 5
// Remove 5 kills from player
local.player addkills -5

atan.example:
// Breakthrough
local.result = atan local.y local.x

// Reborn
local.result = atan local.x
// Result: Principal arc tangent of x, in the interval [-pi/2,+pi/2] radians.

getentity.example:
local.entity = getentity 0
// Entities with entity number between 0 and (sv_maxclients - 1) are reserved for players,
// and thus 'getentity 0' is equal to the client number 0 on the "status" list.

netname.example:
// Breakthrough
local.player_name = local.player.netname
// Reborn
local.player_name = netname local.player

secfireheld.example:
if (local.player.secfireheld) {
	// local.player is holding secondary fire button
}
